import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Studen = props => (
  <tr>
    <td>{props.student.username}</td>
    <td>{props.student.password}</td>
    <td>
      <Link to={"/edit/"+props.exercise._id}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.exercise._id) }}>delete</a>
    </td>
  </tr>
)

export default class StudentList extends Component {
  constructor(props) {
    super(props);

    this.deleteStudent = this.deleteStudent.bind(this)

    this.state = {students: []};
  }

  componentDidMount() {
    axios.get('http://localhost:5000/student/')
      .then(response => {
        this.setState({ students: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  deleteStudent(id) {
    axios.delete('http://localhost:5000/students/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      students: this.state.students.filter(el => el._id !== id)
    })
  }

  studentList() {
    return this.state.students.map(currentstudent => {
      return <Student student={currentstudent} deleteStudent={this.deleteStudent} key={currentstudent._id}/>;
    })
  }

  render() {
    return (
      <div>
        <h3>Logged Students</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Username</th>
            </tr>
          </thead>
          <tbody>
            { this.studentList() }
          </tbody>
        </table>
      </div>
    )
  }
}
