import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Course = props => (
  
    <tr>
    <td>{props.course.professor}</td>
    <td>{props.course.description}</td>
    <td>{props.course.semester}</td>
    <td>{props.course.student}</td>
    <td>
    <Link to={"/edit/"+props.course._id}>edit</Link> | <a href="#" onClick={() => { props.deleteCourse(props.course._id) }}>delete</a>
    </td>
    </tr>

    )

export default class CoursesList extends Component {
  constructor(props) {
    super(props);

    this.deleteCourse = this.deleteCourse.bind(this)

    this.state = {courses: []};
  }

  componentDidMount() {
    axios.get('http://localhost:5000/enrolledcourses/')
      .then(response => {
        console.log(response.data);
        this.setState({ courses: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  deleteCourse(id) {
    axios.delete('http://localhost:5000/courses/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
        Course: this.state.Course.filter(el => el._id !== id)
    })
  }

  courseList() {
    return this.state.courses.map(currentcourse => {
      return <Course sourse={currentcourse} deleteCourse={this.deleteCourse} key={currentcourse._id}/>;
    })
  }

  render() {
    return (
      <div>
        <h3>Logged Courses</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Professor</th>
              <th>Description</th>
              <th>Semester</th>
              <th>Students</th>
            </tr>
          </thead>
          <tbody>
            {this.state.courses.map((elem, i) => {
                <tr>
                        <td>{props.course.professor}</td>
                        <td>{props.course.description}</td>
                        <td>{props.course.semester}</td>
                        <td>{props.course.student}</td>
                        <td>A
                        {/* <Link to={"/edit/"+props.course._id}>edit</Link> | <a href="#" onClick={() => { props.deleteCourse(props.course._id) }}>delete</a> */}
                        </td>
                </tr>
            })}
            { this.courseList() }
          </tbody>
        </table>
      </div>
    )
  }
}
